    private void btnEntrarLoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEntrarLoginActionPerformed
        // TODO add your handling code here:
        String email, senha;
        
        email = txtEmailLogin.getText();
        senha = txtSenhaLogin.getText();
        
        ControllerDTO objecontrollerdto = new ControllerDTO();
        objecontrollerdto.setEmailUsuario(email);
        objecontrollerdto.setSenhaUsuario(senha);
        
        ModelDAO objmodeldao = new ModelDAO();
        boolean loginSucesso = objmodeldao.Login(objecontrollerdto);
        
        if(loginSucesso){
            frmHome Home = new frmHome();
            Home.setVisible(true);
            this.dispose();
        }else{
            txtEmailLogin.setText("");
            txtSenhaLogin.setText("");
            txtEmailLogin.requestFocus();
        }
    }//GEN-LAST:event_btnEntrarLoginActionPerformed

